#### BSM Audit-checking Code by Hal Pomeranz
X
X
sub parse_audit_class {
X
X    # Read the /etc/security/audit_class file for flags and
X    # descriptions.  Store values in %GLOBAL_AUDIT_FLAGS hash
X    # for use in other subroutines.  We could just hard-code
X    # this information into the tester, but this is more flexible.
X
X    my($flag, $desc);
X
X    open(AC, "/etc/security/audit_class") || return(0);
X    while (<AC>) {
X	next if (/^#/ || /^\s*$/ || /:all:/);
X	chomp;
X        ($flag, $desc) = (split(/:/))[1,2];
X	$GLOBAL_AUDIT_FLAGS{$flag} = $desc;
X    }
X    close(AC);
X    return(1);
}
X
sub parse_flag_vector {
X
X    # Take a vector of audit flags ("lo,ad,ex,fm,-fw,-fc,-fd,na")
X    # and parse it out into a hash (hash reference should be specified
X    # as second argument to subroutine).  Hash keys are audit flags 
X    # ("fm", "lo", etc.) and values are 'success' if "+" is set for
X    # flag, 'failure' if "-" is set, or 'all' if neither is specified
X    # (or if both are specified independently in the flag vector).
X    # 
X    # Routine properly handles both "all" flag and the "^" negation
X    # syntax.
X
X    my($vec, $hashref) = @_;
X    my($audit, $not_flag, $char, $flag, @tokens, @chars);
X
X    $vec =~ s/[^-+^,\w]//g;		# strip unexpected chars
X    @tokens = split(/,/, $vec);		# break on commas
X
X    # The trick here is to initially represent the audit state for
X    # a particular flag as a value that can modified easily with
X    # bitwise operators.  Thus "-fm" would initially be represented
X    # as $$hashref{'fm'} = 1, "+fm" is 2, and just "fm" means both
X    # so the value would be 3.  This means we can "add and subtract"
X    # flag settings later in the vector with simple bitwise ops.
X
X    foreach $flag (@tokens) {
X	$not_flag = 0;            	# these are the defaults if
X	$audit = 3;			# no special characters are set
X
X        @chars = split(//, $flag);	# pull off special chars
X        while ($chars[0] !~ /\w/) {
X	    $char = shift(@chars);
X	    if    ($char eq '^') { $not_flag = 1; }
X	    elsif ($char eq '+') { $audit = 2; }
X	    elsif ($char eq '-') { $audit = 1; }
X        }
X        $flag = join('', @chars);	# now put the flag name back together
X
X	# If the flag is "all" then we need to set all possible audit
X	# flags (as obtained from /etc/security/audit_class by the
X	# parse_audit_class() routine).  Otherwise just set the
X	# appropriate value for the given flag
X
X	@flags = ($flag eq 'all') ? keys(%GLOBAL_AUDIT_FLAGS) : ($flag);
X	foreach $flag (@flags) {
X	    if ($not_flag) { $$hashref{$flag} &= (3 - $audit); } 
X            else           { $$hashref{$flag} |= $audit; }
X	}
X    }
X
X    # Now translate the numeric values into human-readable strings
X    # which are more useful in the validate_audit_flags() routine.
X    # Delete any audit entries that were set and then negated out of 
X    # existence later in the flag vector.
X
X    foreach $flag (keys(%{$hashref})) {
X	if    ($$hashref{$flag} == 3) { $$hashref{$flag} = 'all'; }
X	elsif ($$hashref{$flag} == 2) { $$hashref{$flag} = 'successful'; }
X	elsif ($$hashref{$flag} == 1) { $$hashref{$flag} = 'failed'; }
X	else                          { delete($$hashref{$flag}); }
X    }	
}
X
sub validate_audit_flags {
X
X    # First argument is the list of audit flags currently on the
X    # system (set in /etc/security/audit_control).  Second argument
X    # should be the minimum set of audit flags that must be set.
X    #
X    # Return value is 0 if the current audit flags meet or exceed
X    # the minimum recommended set.  Return value is >0 if there are
X    # discrepancies.  Also returns a list of text strings indicating
X    # which settings are a problem.
X
X    my($current, $expected) = @_;
X    my($flag, $msg, @mismatch, %currhash, %exphash);
X
X    parse_flag_vector($current, \%currhash);
X    parse_flag_vector($expected, \%exphash);
X
X    undef(@mismatch);
X    foreach $flag (sort(keys(%exphash))) {
X        next if ($currhash{$flag} eq 'all' || 
X                 $currhash{$flag} eq $exphash{$flag});
X        $msg = 'BSM should at least be auditing ' . $exphash{$flag};
X	$msg .= ' "' . $GLOBAL_AUDIT_FLAGS{$flag} . "\" ($flag) events";
X	push(@mismatch, $msg);
X    }
X
X    return(scalar(@mismatch), @mismatch);
}
