sub security_patch_catalog_good {
X
X    # This routine validates the security_catalog that we'll use with
X    # Security Patch Check, for existence and timeliness.
X
X    # If the catalog is good, it returns the datestamp from the catalog.
X    # If the catalog is bad or non-existent, it returns a 0.
X
X    # This routine takes the directory for the security_catalog as an
X    # argument.
X
X    my $datestamp = 'notfound';
X    my $directory = $_;
X
X    unless ($directory) {
X	&Log("Error: 1.1 Couldn't find security_patch_check directory $directory.");
X    }
X
X    # Check to see if security_catalog is available and up-to-date
X    if ( -f "$directory/security_catalog") {
X		
X	if (open CATALOG,"$directory/security_catalog") {
X	    my $line;
X	    #Throw away the first line, grab the datestamp from the 2nd line.
X	    $line = <CATALOG>;
X	    $line = <CATALOG>;
X	    if ($line =~ /^\s*(\d+)/) {
X		$datestamp = $1;
X	    }
X	    close CATALOG;
X
X	    # This datestamp is the output of perl's time() command, that is,
X	    # the number of seconds since 1/1/1970 (start of Unix epoch)
X
X	    # If it's been more than 60*60*24*30 seconds (1 month), it's too old.
X	    
X	    if ( (time() - $datestamp) <= 2592000 ) {
X		return ($datestamp);
X	    }
X	}
X	#else {
X	#    return(0);
X	#}
X    }
X    #else {
X    #	return(0);
X    #}
X
X    # The code is very simple here -- any of our error conditions would simply
X    # cause the datestamp to not be returned.
X    return(0);
}
