#
# Validate rc.conf and sysctl settings for FreeBSD, 
# sets the score and logs the result.
#
# ARGS
#   ckname - Check name for the global assoc. arrays (hash) weight and score
#   number - Score Question number
#   description - Short Description for the Logs.
#   pos_descr  - True if description is positive (add "No ... " for negative)
#   file - 'rc' = rc.conf or 'sys' = sysctl / sysctl.conf
#   pat - Pattern the variable matches for a positive score.
#   var1 , var2 . . . - One or more variables from rc.conf or sysctl.conf.
X
sub freebsd_check_var {
X
X    # shift first 5, and then extract the vars to check
X    my($ckname, $number, $descript, $pos_descr, $file, $pat) = splice(@_, 0, 6);
X    @vars = @_;
X
X    $weight{$ckname}=1;
X    $score{$ckname}=1;
X    my $var;
X    my $val;
X
X    if ( 'rc' eq $file) { $filenm = "rc.conf"; }
X    else 		{ $filenm = "sysctl.conf"; }
X
X    # How to toggle the description negative or positive"
X    if ($pos_descr) 	{ $neg_pref="No "; 	$pos_pref=""; }
X    else 		{ $neg_pref=""; 	$pos_pref="No "; }
X
X    # If any of the vars do not match, then score is a negative.
X    foreach $var ( @vars ) {
X	if ( 'rc' eq $file ) { 
X	    $val = $GLOBAL_ETC_RC_VALS{$var}; 
X	}
X	else { 
X	    if (open SYSCTL, "sysctl -n " . $var . " |") {
X		@out = <SYSCTL>;
X		close SYSCTL;
X		$val = $out[0];
X		chomp $val;
X	    }
X	}
X
X	if ( $val !~ /^$pat$/ ) {
X	    $score{$ckname}=0;
X	    &Log("Negative: $number $neg_pref$descript ($filenm $var=\"$val\")");
X	}
X    }
X    if ( 1 == $score{$ckname} ) {
X	&Log("Positive: $number $pos_pref$descript.");
X    }
}
